// import Data from '../components/data/ProductData';
import Data from '../components/data/application';
import useDataFetching from './../components/custom_hooks/useDataFetching';
function FetchProducts() {
  //------------------------FetchDataHook------------------//
  const { results, loading } = useDataFetching(
    'https://raw.githubusercontent.com/mwayne16/e-commerce_bicycle/master/src/components/data/application.json'
  );
  if (loading) {
    console.log(results);
    return 'Page is Loading';
  }
  return {
    testState: [...results],
  };
}

const initialState = {
  items: [...data],
  filteredItems: [],
};

const reducer = (state, action) => {
  switch (action.type) {
    case 'filterItems':
      return { ...state, filteredItems: action.payload };
    default:
      return {
        data: state.data,
      };
  }
};

export { initialState, reducer, FetchProducts };
