import React, { useState, useEffect } from 'react';
import './Home.css';

function Hero() {
  const [imgPos, updatePos] = useState({ transform: 'translateY()' });

  const handleParralax = () => {
    const offSet = window.pageYOffset;
    updatePos({ transform: `translateY(${offSet / -6}%)` });
  };
  useEffect(() => {
    window.addEventListener('scroll', handleParralax);
    return () => {
      window.removeEventListener('scroll', handleParralax);
    };
  }, []);
  return (
    <section className="hero">
      <div className="hero-container">
        <div style={imgPos} className="hero-img"></div>
      </div>
    </section>
  );
}

function Arrivals() {
  return <section className="arrivals"></section>;
}

function Home() {
  return (
    <React.Fragment>
      <Hero />
      <Arrivals />
    </React.Fragment>
  );
}

export default Home;

