import React, { useEffect, useState } from 'react';
function Slider(props) {
  const sorted = props.data.sort((a, b) => a.price - b.price);

  let min = sorted[0].price;
  let max = sorted[sorted.length - 1].price;
  const [range, changedRange] = useState(min);
  useEffect(() => {
    props.data.filterByPrice(range);
    return () => {
      props.data.filter(product => product.price >= range);
    };
  }, [props.data, range]);
  return (
    <React.Fragment>
      <input
        onChange={e => changedRange(e.target.value)}
        type="range"
        min={min}
        max={max}
        step="5"
        defaultValue={range}
      />
      <p data-current-price>{'$' + range}</p>
      <p data-max-range>{max}</p>
    </React.Fragment>
  );
}

function ColorSelector(props) {
  const [color, newColor] = useState('');
  props.data
    .map(products => products)
    .filter(product => product.color === color);
  return (
    <>
      <li data-color="black"></li>
      <li data-color="blue"></li>
      <li data-color="green"></li>
      <li data-color="red"></li>
      <li data-color="white"></li>
    </>
  );
}

export { Slider, ColorSelector };
