import React, { useState } from 'react';
import { CartItemsContext } from '../../components/context/cartContext';

function Checkout() {
  let [curQuanity, setQuanity] = useState(1);
  const { products } = React.useContext(CartItemsContext);

  console.log(curQuanity);
  return (
    <section className="checkout">
      <div className="cart-feed">
        <h1 className="checkout-title">My Cart</h1>
        {products.map(({ name, src, price, color, quanity, key }) => (
          <div key={key} className="cart-item">
            <div className="item-overview-wrapper">
              <img src={src} alt={name} />
              <div className="item-details">
                <h2 className="item-name">{name}</h2>
                <p>{`$${price}`}</p>
                <p>Color:{color}</p>
              </div>
            </div>
            <div className="item-price-wrapper">
              <div className="product-item-quanity">
                <label className="quanity-label" htmlFor="quanity-input">
                  Quanity
                </label>
                <div className="quanity-input">
                  <input
                    readOnly
                    type="number"
                    id="quanity-input"
                    min={1}
                    max={99}
                    value={quanity}
                  />
                  <div className="quanity-controls">
                    <span
                      onClick={() => {
                        setQuanity(++curQuanity);
                        ++quanity;
                      }}
                      className="fas fa-chevron-up"
                      id="quanity-up"
                    ></span>
                    <span
                      data-action-block={quanity <= 1 ? true : false}
                      onClick={() =>
                        quanity <= 1 ? quanity : setQuanity(--curQuanity)
                      }
                      className="fas fa-chevron-down"
                      id="quanity-down"
                    ></span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="order-summary">
        <h1 className="checkout-title">Order Summary</h1>
      </div>
    </section>
  );
}
export default Checkout;
