import React, { useReducer, useEffect } from 'react';
import { reducer, initialState } from '../../reducers/productReducer';
import useDataFetching from './../custom_hooks/useDataFetching';

// Try setting the useDataFetching hook here
const ProductContext = React.createContext(null);

const ProductProvider = (props) => {
  const { results, loading } = useDataFetching(
    'https://raw.githubusercontent.com/mwayne16/e-commerce_bicycle/master/src/components/data/application.json'
  );

  useEffect(() => {
    const fetchResults = () => {
      const data = results;
      initialState.items.push(...data);
    };
    fetchResults();
  }, [results]);
  const [products, dispatch] = useReducer(reducer, initialState);
  return loading ? (
    <h1>Loading....</h1>
  ) : (
    <ProductContext.Provider value={{ products, dispatch }}>
      {props.children}
    </ProductContext.Provider>
  );
};
export { ProductContext, ProductProvider };
