import { useState, useEffect } from 'react';

function usePriceFormatter(array) {
  const [prices, formattedPrices] = useState([...array]);
  const formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2,
  });
  useEffect(() => {
    prices.map((values) => formattedPrices(formatter.format(...values)));
    console.log(prices, formattedPrices);
  }, [array, formatter, prices]);
  return { formattedPrices };
}

export default usePriceFormatter;
