import { useEffect, useState, useCallback } from 'react';
import { initialState } from '../../reducers/productReducer';

function useLocalStorage(key) {
  const [state, setState] = useState(
    window.localStorage.getItem(key) !== null || undefined
      ? window.localStorage.getItem(key)
      : null
  );
  const values = [state];

  // Consider just having the key as a useRef with return callbacks for setting and getting data
  console.log(values);
  useEffect(() => {
    window.localStorage.setItem(key, values);
  }, [key, values]);

  return {
    state,
    setState,
    addValue: useCallback((value) => values.push(value), [values]),
  };
}
export default useLocalStorage;
