import React, { useState } from 'react';
import '../layout/styles/ProductPage.css';
import { Link } from 'react-router-dom';
import { CartItemsContext } from './context/cartContext';
import { CartModalContext } from './context/modalContext';
function ProductItemPage(props) {
  return (
    <section className="product-page">
      <ProductItemHeader />
      <ProductItemMain {...props} />
    </section>
  );
}
function ProductItemHeader() {
  return (
    <header className="product-heading">
      <nav className="product-nav">
        <div className="product-breadcrumbs">
          <Link to="/">Home /</Link>
          <Link to="shop">Shop /</Link>
          <p>Product</p>
        </div>
        <div className="navigation-button-container">
          <ul className="navigation-buttons">
            <li>
              <span className="fas fa-chevron-left"></span>Prev
            </li>
            {' | '}
            <li>
              Next <span className="fas fa-chevron-right"></span>
            </li>
          </ul>
        </div>
      </nav>
    </header>
  );
}
function ProductItemMain(props) {
  let [quanity, setQuanity] = useState(1);
  const { products, setProduct } = React.useContext(CartItemsContext);
  const [toggled, setToggle] = React.useContext(CartModalContext);
  const addItemToCart = (item) => {
    setProduct(item);
    setToggle(!toggled);
    props.close();
  };

  const { name, sku, price, color, src, key } = props.location.item;
  console.log(src);
  return (
    !toggled && (
      <main className="product-container">
        <div className="product-item">
          <div
            style={{
              background: `url(${src}) no-repeat center / 85%`,
            }}
            className="product-item-img"
          ></div>
          <div className="product-item-overview">
            <h1 className="product-item-title">{name}</h1>
            <p className="product-item-sku">SKU: {sku}</p>
            <p className="product-item-price">${price}</p>
            {props.children}
            <p className="product-item-colorText">Color: {color}</p>
            <p className="product-item-color" style={{ background: color }}></p>
            <div className="product-item-quanity">
              <label className="quanity-label" htmlFor="quanity-input">
                Quanity
              </label>
              <div className="quanity-input">
                <input
                  readOnly
                  type="number"
                  id="quanity-input"
                  name="quanity-input"
                  min="1"
                  max="99"
                  value={quanity}
                />
                <div className="quanity-controls">
                  <span
                    onClick={() => setQuanity(++quanity)}
                    className="fas fa-chevron-up"
                    id="quanity-up"
                  ></span>
                  <span
                    data-action-block={quanity <= 1 ? true : false}
                    onClick={() =>
                      quanity <= 1 ? quanity : setQuanity(--quanity)
                    }
                    className="fas fa-chevron-down"
                    id="quanity-down"
                  ></span>
                </div>
              </div>
            </div>
            <button
              onClick={addItemToCart.bind(this, [
                ...products,
                { name, sku, price, color, src, key, quanity },
              ])}
              className="product-item-addToCart defaultButton alt-button"
              type="button"
              value={sku}
            >
              Add To Cart
            </button>
          </div>
          {props.children}
        </div>
      </main>
    )
  );
}

export { ProductItemPage, ProductItemHeader, ProductItemMain };
