import React, { useState, useEffect } from 'react';
import './Home.css';
import Butter1 from '../assets/images/butterfly1.png';
import Butter2 from '../assets/images/butterflyGroup.png';
function Hero() {
  const [imgPos, updatePos] = useState({ transform: 'translateY(0px)' });
  const [bflyG, updateFlyG] = useState({ transform: 'translateY(0px)' });
  const [bfly1, updateFly1] = useState({ transform: 'translate(0px, 0px)' });
  const [textPos, updateText] = useState({ transform: 'translateY(0px)' });
  const handleParralax = () => {
    updateFlyG({ transform: 'translateY(' + window.pageYOffset / -1 + '%)' });
    updateFly1({
      transform: 'translate(' + window.pageYOffset / -5 + '%)'
    });
    updateText({ transform: 'translateY(' + window.pageYOffset / -10 + '%)' });
    updatePos({ transform: 'translateY(' + window.pageYOffset / -60 + '%)' });
  };
  useEffect(() => {
    window.addEventListener('scroll', handleParralax);
    return () => {
      window.removeEventListener('scroll', handleParralax);
    };
  }, []);
  return (
    <section className="hero">
      <div className="hero-container">
        <img style={bfly1} src={Butter1} alt="bfly" />
        <img style={bflyG} src={Butter2} alt="bfly1" />
        <div style={imgPos} className="hero-img">
          <h1 style={textPos} className="text">
            This is the forground text against the background image of the
            bicycle. Notice how this text will move faster when the page is
            scrolled than the bike. This is what creates the depth effect known
            as parralax.
          </h1>
        </div>
      </div>
    </section>
  );
}

function Arrivals() {
  return (
    <section className="arrivals">
      <div className=" container"></div>
      <div className=" container"></div>
      <div className=" container"></div>
    </section>
  );
}

function Home() {
  return (
    <React.Fragment>
      <Hero />
      <Arrivals />
    </React.Fragment>
  );
}

export default Home;

