import React, { useState } from 'react';
import '../styles/FAQ.css';
function FAQ() {
  const [toggled, setToggle] = useState();

  const handleToggle = (index) => setToggle(index);
  console.log(toggled);
  return (
    <section className="faq-container">
      <h1 className="section-header">FAQ</h1>
      <h1 className="section-header subheader ">Frequently Asked Questions</h1>
      <div
        tabIndex="1"
        data-expand={toggled === 1 ? true : false}
        className="question-panel-box"
        onClick={(e) => handleToggle(e.target.getAttribute('tabIndex'))}
      >
        <span className="toggle-icon fas fa-chevron-down"></span>
        <h2 className="panel-title">How Long should my service take?</h2>
        <div className="panel-answer">
          <p>
            Typical turnaround time work for all jobs is 10-12 business days
            after clubs arrive at my shop.
          </p>
          <span className="faq-social-icons">
            <ul className="icon-list">
              <li className="fab fa-twitter icon-item"></li>
              <li className="fab fa-facebook-f icon-item"></li>
            </ul>
          </span>
        </div>
      </div>
      <div
        tabIndex="2"
        data-expand={toggled === 2 ? true : false}
        className="question-panel-box"
        onClick={(e) => console.log(e.target.getAttribute('tabIndex'))}
      >
        <span className="toggle-icon fas fa-chevron-down"></span>
        <h2 className="panel-title">What if the parts our out of stock?</h2>
        <div className="panel-answer">
          <p>
            Typical turnaround time work for all jobs is 10-12 business days
            after clubs arrive at my shop.
          </p>
          <span className="faq-social-icons">
            <ul className="icon-list">
              <li className="fab fa-twitter icon-item"></li>
              <li className="fab fa-facebook-f icon-item"></li>
            </ul>
          </span>
        </div>
      </div>
    </section>
  );
}
export default FAQ;

const styles = {
  expanded: {
    display: 'block',
  },
  collapsed: {
    display: 'none',
  },
};
