import React, { useEffect, useState } from 'react';
import useDataFetching from './useDataFetching';
import useArray from './useArray';
function Slider(props) {
  const { results, loading } = useDataFetching(
    'https://raw.githubusercontent.com/mwayne16/e-commerce_bicycle/master/src/application.json'
  );
  const [range, changedRange] = useState(180);
  const { name, price, color } = results;
  const data = useArray(price, range);

  useEffect(() => {
    data.filterByPrice(price);
    return () => {
      console.log(data.filterByPrice(price));
    };
  }, [range]);
  return (
    <React.Fragment>
      <input
        onChange={e => changedRange(e.target.value)}
        type="range"
        min={180}
        max={1000}
        step="8"
        defaultValue={range}
      />
      <p data-current-price>{'$' + range}</p>
      <p data-max-range>{props.max}</p>
    </React.Fragment>
  );
}
export default Slider;
