import React, { useState, useEffect } from 'react';
import './Home.css';

function Hero() {
  const [imgPos, updatePos] = useState({ transform: 'translateY()' });

  const handleParralax = amount => {
    const offSet = window.pageYOffset;
    updatePos({ transform: `translateY(${offSet * amount}%)` });
  };
  useEffect(() => {
    window.addEventListener('scroll', handleParralax.bind(this, -0.02));
    return () => {
      window.removeEventListener('scroll', handleParralax);
    };
  }, []);
  return (
    <section className="hero">
      <div className="hero-container">
        <div style={imgPos} className="hero-img"></div>
      </div>
    </section>
  );
}
// Refactor to make arrivals accept children props
function Arrivals() {
  return (
    <section className="arrivals">
      <h1>New Arrivals</h1>
      <div className="collection-showcase">
        <div className="image-wrapper">
          <img
            src={require('../assets/images/bike2.jpeg')}
            alt="urban bike #1"
          />
          <img
            src={require('../assets/images/bike1.jpeg')}
            alt="urban bike #2"
          />
          <img
            src={require('../assets/images/bike3.jpeg')}
            alt="urban bike #3"
          />
        </div>
        <button type="button" id="collectionBTN" className="defaultButton">
          <h3>View Collection</h3>
        </button>
      </div>
    </section>
  );
}

function About() {
  return (
    <section className="about">
      <div className="about-row-container">
        <div className="about-descblock">
          <h1>About Us</h1>
          <p>
            This is your About section. Itâ€™s a great space to tell your story
            and to describe who you are and what you do. If you're a business,
            talk about how you started and tell the story of your professional
            journey. People want to know the real you, so don't be afraid to
            share personal anecdotes. Explain your core values and how you, your
            organization, or your business stand out from the crowd.
          </p>
          <button className="defaultButton">Visit Us</button>
        </div>
        <div className="about-imgblock"></div>
      </div>
      <div className="about-row-container">
        <div className="about-descblock"></div>
        <div className="about-imgblock"></div>
      </div>
    </section>
  );
}
function Home() {
  return (
    <React.Fragment>
      <Hero />
      <Arrivals />
      <section className="highlighted-quote">
        <h1>
          <span>
            <h1>"</h1>
          </span>
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Laboriosam,
          voluptate consectetur.
          <span>
            <h1>"</h1>
          </span>
        </h1>
      </section>
    </React.Fragment>
  );
}

export default Home;

