import React, { useState, useEffect } from 'react';
import './Home.css';
function Hero() {
  const [imgPos, updatePos] = useState({ transform: 'translateY(0px)' });
  const handleParralax = () => {
    updatePos({ transform: 'translateY(' + imgPos.pageYOffset / -40 + '%)' });
  };
  useEffect(() => {
    window.addEventListener('scroll', handleParralax);
    return () => {
      window.removeEventListener('scroll', handleParralax);
    };
  }, []);
  return (
    <section className="hero">
      <div style={imgPos} className="hero-container"></div>
    </section>
  );
}

function Arrivals() {
  return <section className="arrivals"></section>;
}

function Home() {
  return (
    <React.Fragment>
      <Hero />
      <Arrivals />
    </React.Fragment>
  );
}
export default Home;

