import React, { useState } from 'react';
import useLocalStorage from './custom_hooks/useLocalStorage';
const reducer = (acc, value) => acc + value;

function CartButton(props) {
  const [product] = useLocalStorage('selected-products');
  const [quanity, setQuanity] = useState(reducer(0, product));
  console.log(
    product.reduce((acc, value, index) => {
      return acc + value.item[0].quanity;
    })
  );
  return (
    <button
      onClick={() => console.log(product)}
      style={{ ...styles.button }}
      className="toggleCartWidget"
    >
      <i className="fas fa-shopping-bag">
        <p className="bagCount">{props.itemCount}</p>
      </i>
      <p
        style={{
          fontSize: '12px',
          textTransform: 'uppercase',
          marginTop: '5px',
          fontWeight: '600',
          width: '60px',
        }}
      >
        My Cart
      </p>
    </button>
  );
}

function CartModal(props) {}

function CartWidget() {
  return (
    <React.Fragment>
      <CartButton itemCount={0} />
    </React.Fragment>
  );
}

export default CartWidget;

const styles = {};

styles.button = {
  lineHeight: '4',
  top: '0',
  bottom: '0',
  position: 'relative',
  height: 'auto',
  padding: '0.5em 3em',
  background: '#294057',
  color: '#fff',
};

styles.icon = {
  color: '#B99867',
  fontSize: '2.5em',
};
