import React, { useState, useEffect } from 'react';
import useDataFetching from '../components/useDataFetching';
import './Shop.css';
import '../layout/ProductFilter.css';
import '../layout/SortProducts.css';

function SortProductsCard() {
  const sortMethod = e => {
    console.log(e.target.value);
  };
  return (
    <div className="sort-products">
      <label htmlFor="product-sorter">Sort by</label>
      <select onChange={sortMethod} id="sort">
        <option value="newest">Newest</option>
        <option value="pricelth">Price (low to high)</option>
        <option value="pricehtl">Price (high to low)</option>
        <option value="nameaz">Name A-Z</option>
        <option value="nameza">Name Z-A</option>
      </select>
    </div>
  );
}

function FilterCard(props) {
  const { results, loading } = useDataFetching(
    'https://raw.githubusercontent.com/mwayne16/e-commerce_bicycle/master/src/application.json'
  );

  let min, max;
  const findMinMax = (minim, maxim) => {
    console.log('hit');
    try {
      if (minim) {
        let minPrice = results.reduce((min, max) => {
          return min.price < max.price ? min : max;
        });
        return (min = minPrice);
      }
      if (maxim) {
        let maxPrice = results.reduce((min, max) => {
          return min.price < max.price ? max : min;
        });
        max = maxPrice;
      }
    } catch (error) {
      return console.log(error);
    }
  };
  useEffect(
    () => {
      findMinMax(true, true);
    },
    min,
    max,
    results
  );
  return (
    <div className="product-filter">
      <h2>Filter By</h2>
      <hr />
      <div className="price-filter">
        <p>Price</p>
        <span
          id="price"
          className="price-toggle fas fa-minus"
          data-state="expand"
        ></span>
        <div className="price-slider">
          <input
            onChange={null}
            type="range"
            min={min}
            max={max}
            defaultValue={min}
          />
          <p data-current-price={props.current}>{min}</p>
          <p data-max-range={props.max}>{max}</p>
        </div>
      </div>
      <hr />
      <div className="color-filter">
        <p>Color</p>
        <span
          id="color"
          className="color-toggle fas fa-plus"
          data-state="expand"
        ></span>
        <div className="colorselector">
          <ul>
            <li data-color="black"></li>
            <li data-color="blue"></li>
            <li data-color="green"></li>
            <li data-color="red"></li>
            <li data-color="white"></li>
          </ul>
        </div>
      </div>
    </div>
  );
}

function Products(props) {
  return (
    <div className="product-items-container">
      <SortProductsCard />
      <ul>{props.children}</ul>
    </div>
  );
}
function Shop() {
  const { results, loading } = useDataFetching(
    'https://raw.githubusercontent.com/mwayne16/e-commerce_bicycle/master/src/application.json'
  );
  if (loading) {
    return 'Loading...';
  }
  return (
    <section className="product-collection">
      <h1 className="collection-header">Our Collection</h1>
      <section className="product-gallery">
        <FilterCard />
        <Products>
          {results.map(({ name, key, price }) => (
            <li key={key}>
              <div className="product-item">
                <img
                  src={require(`../assets/images/product${key}.jpeg`)}
                  alt="product1"
                />
                <span className="item-details">
                  <p data-item-title={name}>{name}</p>
                  <p data-item-price={price}>{'$' + price}</p>
                </span>
              </div>
            </li>
          ))}
        </Products>
      </section>
    </section>
  );
}
export default Shop;

