import React, { useState, useEffect, useReducer, useContext } from 'react';
import { Slider, ColorSelector, SortProductsCard } from '../components/Filters';
import { initialState, reducer } from '../reducers/productItems';
import useDataFetching from '../components/useDataFetching';
import { Transition } from 'react-transition-group';
import './Shop.css';
import '../layout/ProductFilter.css';
const ProductContext = React.createContext(null);
// Use Ref doesn't work, wont re-render DOM
// useState likely will work but would require two seperate state calls
// Having two useRef's for each filter element and then a useState toggle may work, but would then require three lines of code opposed to two.
// Bring Price Slider and Color filter inside the filter type container, then just control height
// Set a style attribute within the filter-content that has a delay of 1s before setting no none
function FilterCard() {
  const toggledStyles = {
    entering: {
      opacity: 0,
      transform: 'translateY(-100%)',
    },
    entered: {
      maxHeight: '200px',
      transform: 'translateY(0%)',
      opacity: 1,
      zIndex: 1,
    },
    exiting: {
      transform: 'translateY(-100%)',
      opacity: 0,
      maxHeight: '0px',
      zIndex: -1,
    },
    exited: {
      position: 'absolute',
      transform: 'translateY(-100%)',
      maxHeight: '0px',
      opacity: 0,
      zIndex: -1,
    },
  };
  const { ...props } = useContext(ProductContext);
  const [toggled, updateToggle] = useState({ price: true, color: false });

  return (
    <div className="product-filter">
      <SortProductsCard {...props} />
      <div className="filter-options">
        <h2>Filter By</h2>
        <div data-is-toggled={toggled.price} className="filter-type">
          <div className="filter-type-header">
            <p>Price</p>
            <span
              onClick={() =>
                updateToggle({ ...toggled, price: !toggled.price })
              }
              id={'price-toggle'}
              className={`filter-toggle-action ${
                toggled.price ? 'fas fa-minus' : 'fas fa-plus'
              } `}
            ></span>
          </div>
          <Transition
            enter={toggled.price}
            exit={!toggled.price}
            appear={toggled.price}
            in={toggled.price}
            timeout={300}
          >
            {(state) => (
              <div
                style={{
                  ...toggledStyles[state],
                  transition: '.3s linear',
                }}
                className="filter-content"
                id="price-slider"
                data-is-toggled={toggled.price}
              >
                <Slider {...props} />
              </div>
            )}
          </Transition>
        </div>

        <div data-is-toggled={toggled.color} className="filter-type">
          <div className="filter-type-header">
            <p>Color</p>
            <span
              onClick={() =>
                updateToggle({ ...toggled, color: !toggled.color })
              }
              id="color-toggle"
              className={`filter-toggle-action ${
                toggled.color ? 'fas fa-minus' : 'fas fa-plus'
              } `}
            ></span>
          </div>
          <Transition
            enter={toggled.color}
            exit={!toggled.color}
            appear={toggled.color}
            in={toggled.color}
            timeout={300}
          >
            {(state) => (
              <div
                style={{
                  ...toggledStyles[state],
                  transition: '.4s ease-in-out',
                }}
                className="filter-content"
                id="color-filter"
                data-is-toggled={toggled.color}
              >
                <ul className="color-filter-list">
                  <ColorSelector {...props} />
                </ul>
              </div>
            )}
          </Transition>
        </div>
      </div>
    </div>
  );
}

function Products(props) {
  return (
    <div className="product-items-container">
      <ul>
        {props.items.map(({ name, key, price, released, src }) => (
          <li key={key}>
            <div key={key} className="product-item">
              <img src={src} alt="product1" />
              <span className="item-details">
                <p data-item-title={name}>{name}</p>
                <p data-item-price={price}>{'$' + price}</p>
              </span>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
function ProductGalleryContainer() {
  const [product, dispatch] = useReducer(reducer, initialState);
  const [filter, setFilter] = useState(product.items);
  useEffect(() => {
    setFilter(
      product.filteredItems.length !== 0 ? product.filteredItems : product.items
    );
  }, [product]);
  return (
    <section className="product-gallery">
      <ProductContext.Provider value={{ product, dispatch }}>
        <FilterCard />
        <Products items={filter} />
      </ProductContext.Provider>
    </section>
  );
}
function Shop(props) {
  //------------------------FetchDataHook------------------//
  // const { results, loading } = useDataFetching(
  //   'https://raw.githubusercontent.com/mwayne16/e-commerce_bicycle/master/src/application.json'
  // );

  return (
    <section className="product-collection">
      <h1 className="collection-header">Our Collection</h1>
      <ProductGalleryContainer />
    </section>
  );
}
export { Shop, ProductContext };

