import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Shop.css';

function Products() {
  const [products, updateProducts] = useState([]);
  useEffect(() => {
    const abortController = new AbortController();
    const signal = abortController.signal;
    axios
      .get(
        'https://raw.githubusercontent.com/mwayne16/e-commerce_bicycle/master/src/application.json',
        {
          signal
        }
      )
      .then(res => {
        const filter = arr =>
          arr.reduce(
            product => product.price < 300 && product.color === 'black'
          );
        updateProducts(filter([...res.data]));
      })
      .catch(err => new Error(err));
    return () => {
      abortController.abort();
    };
  }, []);

  return (
    <ul>
      {products.map(product => (
        <li key={product.key}>
          <img
            src={require(`../assets/images/product${product.key}.jpeg`)}
            alt="urban bike #1"
          />
          {product.name} - ${product.price}
        </li>
      ))}
    </ul>
  );
}

function Shop() {
  return (
    <section className="product-collection">
      <h1 className="collection-header">Our Collection</h1>
      <Products></Products>
    </section>
  );
}
export default Shop;
