import React, { useState, useEffect, useContext } from 'react';
import { ProductContext } from '../layout/Shop';
function Slider() {
  const { product, dispatch } = useContext(ProductContext);
  const sorted = product.items.sort((a, b) => a.price - b.price);
  const min = sorted[0].price;
  const max = sorted[sorted.length - 1].price;
  const [range, changedRange] = useState(min);

  useEffect(() => {
    return () => {
      const filter = arr => arr.filter(product => product.price >= range);
      dispatch({ type: 'filterByPrice', payload: filter(product.items) });
    };
  }, [range]);
  return (
    <>
      <input
        onChange={e => changedRange(e.target.value)}
        type="range"
        min={min}
        max={max}
        step="5"
        defaultValue={range}
      />
      <p data-current-price>{'$' + range}</p>
      <p data-max-range>
        {max}
        {}
      </p>
    </>
  );
}

function ColorSelector(props) {
  const { product, dispatch } = useContext(ProductContext);
  const [color, newColor] = useState('red');
  useEffect(() => {
    return () => {
      const filter = arr => arr.filter(product => product.color === color);
      dispatch({ type: 'filterByColor', payload: filter(product.items) });
    };
  }, [input]);

  return (
    <>
      <li data-color="black"></li>
      <li data-color="blue"></li>
      <li data-color="green"></li>
      <li data-color="red"></li>
      <li data-color="white"></li>
    </>
  );
}

function SortProductsCard() {
  const sortMethod = e => {};
  return (
    <div className="sort-products">
      <label htmlFor="product-sorter">Sort by</label>
      <select onChange={sortMethod} id="sort">
        <option value="newest">Newest</option>
        <option value="pricelth">Price (low to high)</option>
        <option value="pricehtl">Price (high to low)</option>
        <option value="nameaz">Name A-Z</option>
        <option value="nameza">Name Z-A</option>
      </select>
    </div>
  );
}
export { Slider, ColorSelector, SortProductsCard };

