import React, { useEffect } from 'react';
function ScrollToTop(props) {
  useEffect(() => {
    console.log('ran');
    window.scrollTo({
      top: 0,
      left: 0,
      behavior: 'smooth',
    });
  });
  return props.children;
}
export default React.memo(ScrollToTop, (prevProps, nextProps) => {
  return nextProps.location.pathname !== prevProps.location.pathname;
});
