import React, { useEffect, useState } from 'react';
import Data from './data/ProductData';
import useArray from './useArray';
const data = useArray([...Data]);
function Slider(props) {
  const sorted = data.value.sort((a, b) => a.price - b.price);
  const [range, changedRange] = useState(sorted[0].price);
  useEffect(() => {
    return () => {
      data.value
        .map(products => {
          return products;
        })
        .filter(product => product.price >= range);
    };
  }, [props.color, data.value, range]);
  return (
    <React.Fragment>
      <input
        onChange={e => changedRange(e.target.value)}
        type="range"
        min={sorted[0].price}
        max={sorted[sorted.length - 1].price}
        step="8"
        defaultValue={range}
      />
      <p data-current-price>{'$' + range}</p>
      <p data-max-range>{props.max}</p>
    </React.Fragment>
  );
}

function ColorSelector() {
  const [color, newColor] = useState('');
  console.log(
    data.value
      .map(products => products)
      .filter(product => product.color === color)
  );
  return (
    <>
      <li data-color="black"></li>
      <li data-color="blue"></li>
      <li data-color="green"></li>
      <li data-color="red"></li>
      <li data-color="white"></li>
    </>
  );
}

export default Slider;
