import React, { useState } from 'react';
import { CartItemsContext } from './cartContext';
import { CartModalContext } from './modalContext';
import '../../layout/styles/CartModal.css';
import { CSSTransition } from 'react-transition-group';
function CartButton(props) {
  const [toggled, setToggle] = React.useContext(CartModalContext);
  return (
    <button
      onClick={() => setToggle(!toggled)}
      style={{ ...styles.button }}
      className="toggleCartWidget"
    >
      <i className="fas fa-shopping-bag">
        <p className="bagCount">{props.itemCount}</p>
      </i>
      <p
        style={{
          fontSize: '12px',
          textTransform: 'uppercase',
          marginTop: '5px',
          fontWeight: '600',
          width: '60px',
        }}
      >
        My Cart
      </p>
    </button>
  );
}

function CartModal() {
  const { products, setProduct, subTotal } = React.useContext(CartItemsContext);
  const [toggled, setToggle] = React.useContext(CartModalContext);
  const [transition, setTransition] = useState(false);

  const handleModal = () => {
    setTransition(!transition);
    setTimeout(() => {
      setToggle(!toggled);
    }, 1000);
  };
  const modalStyles = {
    modalExit: {
      animation: 'modal-onExit 1s forwards',
    },
    arrowExit: {
      animation: 'modalArrow-onExit 1s forwards',
    },
  };
  return (
    <aside
      style={transition ? modalStyles.modalExit : null}
      className="cart-popout-modal"
    >
      <header className="cart-modal-header">
        <span
          style={transition ? modalStyles.arrowExit : null}
          onClick={handleModal}
          className="fas fa-chevron-right"
          data-modal-toggle="false"
        ></span>
        <h1>Cart</h1>
      </header>
      <main>
        <div className="cart-modal-product">
          <ul>
            {products.map(({ sku, name, quanity, price, src, key }) => (
              <li className="cart-modal-item" key={key}>
                <div className="item-wrapper">
                  <aside
                    className="item-image"
                    style={{
                      background: `url(${src}) no-repeat left center / 80px 80px  `,
                    }}
                  ></aside>
                  <div className="item-info">
                    <h2>{name}</h2>
                    <p>QTY:{quanity}</p>
                    <p>${price}</p>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </main>
      <div className="cart-modal-total">
        <h1>Subtotal</h1>
        <h1>${subTotal.toFixed(2)}</h1>
      </div>
      <footer className="modal-footer">
        <button
          className="product-modal-addToCart defaultButton alt-button"
          type="submit"
        >
          Add To Cart
        </button>
      </footer>
    </aside>
  );
}

function CartWidget() {
  const { products } = React.useContext(CartItemsContext);
  const count =
    products.length >= 1
      ? products.reduce((acc, current) => ({
          quanity: acc.quanity + current.quanity,
        }))
      : 0;

  return (
    <React.Fragment>
      <CartButton itemCount={count.quanity || count} />
    </React.Fragment>
  );
}

export { CartWidget, CartModal };

const styles = {
  button: {
    lineHeight: '4',
    top: '0',
    bottom: '0',
    position: 'relative',
    height: 'auto',
    padding: '0.5em 3em',
    background: '#294057',
    color: '#fff',
  },
};
