import React from 'react';
import ReactDOM from 'react-dom';
import '../layout/ProductModal.css';
// Lift state up the same way I did with the color selector to filter card component
function ProductModal(props) {
  const modal = document.querySelector('#root-product-modal');

  const previewedProduct = props.items.find(
    (product) => product.key === props.selectedProduct
  );
  const { name, sku, price, color, src } = previewedProduct;
  return ReactDOM.createPortal(
    <div className="product-modal">
      <div className="product-modal-img">
        <img src={src} alt="productimage" />
      </div>
      <div className="product-modal-overview">
        <h1>{name}</h1>
        <p>SKU: {sku}</p>
        <p>{price}</p>
        <span>view full details</span>
        <p>Color:{color}</p>
        <p style={{ background: color }}>Color</p>
        <div>Quanity Box</div>
        <button
          onClick={() => {
            props.close(true);
            modal.datset.toggled = false;
          }}
        >
          Add To Cart
        </button>
      </div>
    </div>,
    modal
  );
}
export default ProductModal;
