import React, { useState, useEffect } from 'react';
import useDataFetching from './useDataFetching';
function useFilteredData(FilterBy) {
  const [method, changeMethod] = useState(0);
  const { results, loading } = useDataFetching(
    'https://raw.githubusercontent.com/mwayne16/e-commerce_bicycle/master/src/application.json'
  );
  useEffect(() => {
    async function Filter() {
      let data = results;
      if (loading) {
        return;
      }
      try {
        let findMinPrice = await data.reduce((min, max) =>
          method.min < method.max ? min : max
        );
        data
          .filter(product => product.price - product.sale <= findMinPrice.price)
          .map(product => console.log(product));
      } catch (error) {
        console.log(error);
      }
    }
    Filter();
    return () => {};
  }, [FilterBy, results, loading]);

  return <div></div>;
}
export default useFilteredData;
