import React, { useState, useEffect } from 'react';
import './Home.css';

function Hero() {
  const [imgPos, updatePos] = useState({ transform: 'translateY()' });

  const handleParralax = amount => {
    const offSet = window.pageYOffset;
    updatePos({ transform: `translateY(${offSet * amount}%)` });
  };
  useEffect(() => {
    window.addEventListener('scroll', handleParralax.bind(this, -0.02));
    return () => {
      window.removeEventListener('scroll', handleParralax);
    };
  }, []);
  return (
    <section className="hero">
      <div className="hero-container">
        <div style={imgPos} className="hero-img"></div>
      </div>
    </section>
  );
}

// Refactor to make arrivals accept children props
function Arrivals() {
  return (
    <section className="arrivals">
      <h1>New Arrivals</h1>
      <div className="collection-showcase">
        <img src={require('../assets/images/bike2.jpeg')} alt="urban bike #1" />
        <img src={require('../assets/images/bike1.jpeg')} alt="urban bike #2" />
        <img src={require('../assets/images/bike3.jpeg')} alt="urban bike #3" />
      </div>
    </section>
  );
}

function Home() {
  return (
    <React.Fragment>
      <Hero />
      <Arrivals />
    </React.Fragment>
  );
}

export default Home;

