import React from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Header from './layout/pages/Header';
import Footer from './layout/pages/Footer';
import Home from './layout/pages/Home';
import { Shop } from './layout/pages/Shop';
import { CartProvider } from './components/context/cartContext';
import './layout/styles/App.css';
import { ModalProvider } from './components/context/modalContext';
import { ProductProvider } from './components/context/productContext';
import { ProductItemPage } from './components/ProductItemPage';

function App() {
  return (
    <Router>
      <div className="App">
        <ProductProvider>
          <CartProvider>
            <ModalProvider>
              <Header />
              <Switch>
                <Route exact path="/">
                  <Home />
                </Route>
                <Route path="/shop">
                  <Shop />
                </Route>
                <Route path="/productitempage">
                  <ProductItemPage>
                    <div className="product-description">
                      <p>
                        I'm a product description. This is a great place to
                        "sell" your product and grab buyers' attention. Describe
                        your product clearly and concisely. Use unique keywords.
                        Write your own description instead of using
                        manufacturers' copy.
                      </p>
                    </div>
                  </ProductItemPage>
                  >
                </Route>
              </Switch>
              <Footer />
            </ModalProvider>
          </CartProvider>
        </ProductProvider>
      </div>
    </Router>
  );
}

export default App;
