import React, { useState } from 'react';
import { CartItemsContext } from './cartContext';
import { CartModalContext } from './modalContext';

function CartButton(props) {
  const [toggled, setToggle] = React.useContext(CartModalContext);
  return (
    <button
      onClick={() => {
        setToggle(!toggled);
        props.logProduct();
      }}
      style={{ ...styles.button }}
      className="toggleCartWidget"
    >
      <i className="fas fa-shopping-bag">
        <p className="bagCount">{props.itemCount}</p>
      </i>
      <p
        style={{
          fontSize: '12px',
          textTransform: 'uppercase',
          marginTop: '5px',
          fontWeight: '600',
          width: '60px',
        }}
      >
        My Cart
      </p>
    </button>
  );
}

function CartModal() {
  // Add item combining on duplicate items in useLocalStorage
  return (
    <div
      style={{
        background: 'red',
        width: '500px',
        height: '500px',
        position: 'absolute',
      }}
    >
      Cart Modal
    </div>
  );
}

function CartWidget() {
  const [product] = React.useContext(CartItemsContext);
  const count =
    product.length >= 1
      ? product.reduce((acc, current) => ({
          quanity: acc.quanity + current.quanity,
        }))
      : 0;

  const logProduct = () => console.log(product);

  return (
    <React.Fragment>
      <CartButton log={logProduct} itemCount={count.quanity || count} />
    </React.Fragment>
  );
}

export { CartWidget, CartModal };

const styles = {};

styles.button = {
  lineHeight: '4',
  top: '0',
  bottom: '0',
  position: 'relative',
  height: 'auto',
  padding: '0.5em 3em',
  background: '#294057',
  color: '#fff',
};
