import React from 'react';
import useModalToggle from '../custom_hooks/useModalToggle';
import ProductModal from './ProductModal';

// Perhaps the context could be in the useModal hook so that the modal being used is dynamic to the isntance of the hook call
let ModalContext;
const { Provider } = (ModalContext = React.createContext(null));
const ModalProvider = (props) => {
  const { toggled, handleModal, modal } = useModalToggle();

  return (
    <Provider value={{ toggled, handleModal, modal }}>
      {modal.type !== null && modal.type.name === ProductModal && (
        <ProductModal />
      )}
      {props.children}
    </Provider>
  );
};
