import ReactDOM from 'react-dom';
import React, { useEffect } from 'react';
function Modal(props) {
  useEffect(() => {
    console.log(props.toggled);
    props.location.dataset.toggled =
      props.toggled === 'undefined' ? false : props.toggled;
  });

  return ReactDOM.createPortal(props.children, props.location);
}
export default Modal;
