import React, { useState, useEffect } from 'react';
import useDataFetching from './useDataFetching';
function Slider({ count, setCount, props }) {
  const results = useDataFetching(
    'https://raw.githubusercontent.com/mwayne16/e-commerce_bicycle/master/src/application.json'
  );
  const rand = [1, 2, 3, 4];
  const {
    data: [data, updateResults]
  } = {
    data: useState(results),
    ...(props.state || [])
  };

  [count, setCount] = {
    count: useState(0),
    ...(props.state || {})
  };

  const sorted = rand.sort((a, b) => a.price - b.price);
  let min = sorted[0].price;
  let max = sorted[sorted.length - 1].price;
  const [range, changedRange] = useState(min);

  useEffect(() => {
    rand.filter(product => product.price >= range);
    return () => {};
  });
  return (
    <>
      <button onClick={() => setCount(count + 1)}>Increment Count</button>
      <input
        onChange={e => changedRange(e.target.value)}
        type="range"
        min={min}
        max={max}
        step="5"
        defaultValue={range}
      />
      <p data-current-price>{'$' + range}</p>
      <p data-max-range>
        {max}
        {}
      </p>
    </>
  );
}

function ColorSelector(props) {
  const [color, newColor] = useState('');
  const data = [1, 2, 3, 4, 5, 6];
  data.map(products => products).filter(product => product.color === color);
  return (
    <>
      <li data-color="black"></li>
      <li data-color="blue"></li>
      <li data-color="green"></li>
      <li data-color="red"></li>
      <li data-color="white"></li>
    </>
  );
}

function SortProductsCard() {
  const sortMethod = e => {};
  return (
    <div className="sort-products">
      <label htmlFor="product-sorter">Sort by</label>
      <select onChange={sortMethod} id="sort">
        <option value="newest">Newest</option>
        <option value="pricelth">Price (low to high)</option>
        <option value="pricehtl">Price (high to low)</option>
        <option value="nameaz">Name A-Z</option>
        <option value="nameza">Name Z-A</option>
      </select>
    </div>
  );
}
export { Slider, ColorSelector, SortProductsCard };

