import React from 'react';
import useLocalStorage from '../custom_hooks/useLocalStorage';

const CartItemsContext = React.createContext();
const CartProvider = (props) => {
  const [product, setProduct] = useLocalStorage('selected-products', []);

  // const subTotal = product.reduce(
  //   (acc, curr) => acc.price * acc.quanity + curr.price * curr.quanity
  // );
  const subTotal = product.reduce((acc, current) => ({
    price: acc.price * acc.quanity + current.price * current.quanity,
  }));
  console.log(subTotal.price);
  return (
    <CartItemsContext.Provider value={{ product, setProduct }}>
      {props.children}
    </CartItemsContext.Provider>
  );
};

export { CartItemsContext, CartProvider };
