import React, { useState, useEffect } from 'react';
import useDataFetching from './useDataFetching';
function useFilteredData(FilterBy) {
  const [method, changeMethod] = useState();
  const { results } = useDataFetching(
    'https://raw.githubusercontent.com/mwayne16/e-commerce_bicycle/master/src/application.json'
  );
  useEffect(() => {
    async function Filter() {
      try {
        results
          .map(({ name, color, price }) => {
            return console.log(name, color, price);
          })
          .filter(price => price.price <= 180);
      } catch (error) {
        console.log(error);
      }
    }
    Filter();
    return () => {};
  }, [FilterBy, results]);

  return <div></div>;
}
export default useFilteredData;
