import React, { useState, useEffect } from 'react';
import useDataFetching from './useDataFetching';
function useFilteredData(FilterBy) {
  const [method, changeMethod] = useState();
  const { results } = useDataFetching(
    'https://raw.githubusercontent.com/mwayne16/e-commerce_bicycle/master/src/application.json'
  );
  useEffect(() => {
    async function Filter() {
      try {
        let findMinPrice = results.reduce((min, max) =>
          min.price < max.price ? min : max
        );
        results
          .filter(product => product.price <= findMinPrice.price)
          .map(({ name, price, color }) => console.log(name, price, color));
      } catch (error) {
        console.log(error);
      }
    }
    Filter();
    return () => {};
  }, [FilterBy, results]);

  return <div></div>;
}
export default useFilteredData;
