import React from 'react';
import useLocalStorage from '../custom_hooks/useLocalStorage';

let CartContext;
const { Provider } = (CartContext = React.createContext());
const CartProvider = (props) => {
  const [product, addProduct] = useLocalStorage('selected-products', []);

  return <Provider value={[product, addProduct]}>{props.children}</Provider>;
};

export { CartContext, CartProvider };
